//! Tests for ethereum-backing.

// --- substrate ---
use frame_support::{assert_err, assert_ok};
use frame_system::RawOrigin;
use sp_runtime::{traits::Dispatchable, AccountId32};
// --- darwinia ---
use crate::*;
use array_bytes::hex_bytes_unchecked;
use darwinia_staking::{RewardDestination, StakingBalance, StakingLedger, TimeDepositItem};
use darwinia_support::balance::lock::StakingLock;
use ethereum_primitives::{
	header::EthereumHeader, receipt::EthereumReceiptProof, EthereumNetworkType,
};

type EthereumRelay = darwinia_ethereum_linear_relay::Module<Test>;

decl_tests!();

parameter_types! {
	pub const EthereumLinearRelayModuleId: ModuleId = ModuleId(*b"da/ethli");
	pub const EthereumNetwork: EthereumNetworkType = EthereumNetworkType::Ropsten;
}
impl darwinia_ethereum_linear_relay::Trait for Test {
	type ModuleId = EthereumLinearRelayModuleId;
	type Event = ();
	type EthereumNetwork = EthereumNetwork;
	type Call = Call;
	type Currency = Ring;
	type WeightInfo = ();
}

pub struct ExtBuilder;
impl Default for ExtBuilder {
	fn default() -> Self {
		Self
	}
}
impl ExtBuilder {
	pub fn build(self) -> sp_io::TestExternalities {
		let mut t = frame_system::GenesisConfig::default()
			.build_storage::<Test>()
			.unwrap();

		GenesisConfig::<Test> {
			token_redeem_address: fixed_hex_bytes_unchecked!(
				"0x49262B932E439271d05634c32978294C7Ea15d0C",
				20
			)
			.into(),
			deposit_redeem_address: fixed_hex_bytes_unchecked!(
				"0x6EF538314829EfA8386Fc43386cB13B4e0A67D1e",
				20
			)
			.into(),
			set_authorities_address: fixed_hex_bytes_unchecked!(
				"0xE4A2892599Ad9527D76Ce6E26F93620FA7396D85",
				20
			)
			.into(),
			ring_token_address: fixed_hex_bytes_unchecked!(
				"0xb52FBE2B925ab79a821b261C82c5Ba0814AAA5e0",
				20
			)
			.into(),
			kton_token_address: fixed_hex_bytes_unchecked!(
				"0x1994100c58753793D52c6f457f189aa3ce9cEe94",
				20
			)
			.into(),
			ring_locked: 20000000000000,
			kton_locked: 5000000000000,
		}
		.assimilate_storage(&mut t)
		.unwrap();

		t.into()
	}
}

#[test]
fn genesis_linear_config_works() {
	ExtBuilder::default().build().execute_with(|| {
		assert_eq!(EthereumBacking::pot::<Ring>(), 20000000000000);
		assert_eq!(EthereumBacking::pot::<Kton>(), 5000000000000);
	});
}

#[test]
fn verify_linear_parse_token_redeem_proof() {
	ExtBuilder::default()
		.build()
		.execute_with(|| {
			assert_ok!(EthereumRelay::set_number_of_blocks_safe(RawOrigin::Root.into(), 0));

			// https://ropsten.etherscan.io/tx/0x1d3ef601b9fa4a7f1d6259c658d0a10c77940fa5db9e10ab55397eb0ce88807d
			let proof_record = EthereumReceiptProof {
				index: 0x12,
				proof: hex_bytes_unchecked(""),
				header_hash: fixed_hex_bytes_unchecked!("0xabf627ce77d9f92a40f34e3cace721c3f089000dae820d00d3e99314c263a0c3", 32).into()
			};

			let header : EthereumHeader = serde_json::from_str(r#"{"parent_hash":"0xd55ce7660d0161c38b34015ce5468e1661f1c77865f23415e246ac9ccf7b2b22","timestamp":1599124448,"number":8610261,"author":"0xad87c0e80ab5e13f15757d5139cc6c6fcb823be3","transactions_root":"0x6cf40dbc3f8ce55ffc0f863a65ffd285da787b77af952c319e2577c5ab278a3a","uncles_hash":"0x1dcc4de8dec75d7aab85b567b6ccd41ad312451b948a7413f0a142fd40d49347","extra_data":"0xde830207028f5061726974792d457468657265756d86312e34312e30826c69","state_root":"0xf108de6fa8fbf18e795cb3e1911d32e7d26178a69bb2f365662c8cba58bd7159","receipts_root":"0xfdb6d2cdb6bc9e78711a008b2912e62db28de4520dfcd49bd6f7086718c5d0cb","log_bloom":"","gas_used":954909,"gas_limit":8000029,"difficulty":515540132,"seal":["0xa0deadf98810a6ccfb8d00e8f6bc7ad7f5d62d5a42760c0d9db8a549df76697704","0x88bf7326c26c57c69c"],"hash":"0xabf627ce77d9f92a40f34e3cace721c3f089000dae820d00d3e99314c263a0c3"}"#).unwrap();

			assert_ok!(EthereumRelay::init_genesis_header(&header, 31419688206738532));

			let expect_account_id = EthereumBacking::account_id_try_from_bytes(
				&hex_bytes_unchecked("0xe44664996ab7b5d86c12e9d5ac3093f5b2efc9172cb7ce298cd6c3c51002c318"),
			).unwrap();

			assert_eq!(
				EthereumBacking::parse_token_redeem_proof(&proof_record),
				Ok((expect_account_id, (true, 1234500000), 0)),
			);
		});
}

#[test]
fn verify_linear_redeem_ring() {
	ExtBuilder::default()
		.build()
		.execute_with(|| {
			assert_ok!(EthereumRelay::set_number_of_blocks_safe(RawOrigin::Root.into(), 0));

			// https://ropsten.etherscan.io/tx/0x1d3ef601b9fa4a7f1d6259c658d0a10c77940fa5db9e10ab55397eb0ce88807d
			let proof_record = EthereumReceiptProof {
				index: 0x12,
				proof: hex_bytes_unchecked(""),
				header_hash: fixed_hex_bytes_unchecked!("0xabf627ce77d9f92a40f34e3cace721c3f089000dae820d00d3e99314c263a0c3", 32).into()
			};

			let header : EthereumHeader = serde_json::from_str(r#"{"parent_hash":"0xd55ce7660d0161c38b34015ce5468e1661f1c77865f23415e246ac9ccf7b2b22","timestamp":1599124448,"number":8610261,"author":"0xad87c0e80ab5e13f15757d5139cc6c6fcb823be3","transactions_root":"0x6cf40dbc3f8ce55ffc0f863a65ffd285da787b77af952c319e2577c5ab278a3a","uncles_hash":"0x1dcc4de8dec75d7aab85b567b6ccd41ad312451b948a7413f0a142fd40d49347","extra_data":"0xde830207028f5061726974792d457468657265756d86312e34312e30826c69","state_root":"0xf108de6fa8fbf18e795cb3e1911d32e7d26178a69bb2f365662c8cba58bd7159","receipts_root":"0xfdb6d2cdb6bc9e78711a008b2912e62db28de4520dfcd49bd6f7086718c5d0cb","log_bloom":"","gas_used":954909,"gas_limit":8000029,"difficulty":515540132,"seal":["0xa0deadf98810a6ccfb8d00e8f6bc7ad7f5d62d5a42760c0d9db8a549df76697704","0x88bf7326c26c57c69c"],"hash":"0xabf627ce77d9f92a40f34e3cace721c3f089000dae820d00d3e99314c263a0c3"}"#).unwrap();

			assert_ok!(EthereumRelay::init_genesis_header(&header, 31419688206738532));

			let expect_account_id = EthereumBacking::account_id_try_from_bytes(
				&hex_bytes_unchecked("0xe44664996ab7b5d86c12e9d5ac3093f5b2efc9172cb7ce298cd6c3c51002c318"),
			).unwrap();
			let id1 = AccountId32::from([0; 32]);
			let ring_locked_before = EthereumBacking::pot::<Ring>();
			let _ = Ring::deposit_creating(&expect_account_id, 1);

			assert_ok!(EthereumBacking::redeem(
				Origin::signed(id1.clone()),
				RedeemFor::Token,
				proof_record.clone()
			));
			assert_eq!(Ring::free_balance(&expect_account_id), 1234500000 + 1);

			let ring_locked_after = EthereumBacking::pot::<Ring>();
			assert_eq!(ring_locked_after + 1234500000, ring_locked_before);

			// shouldn't redeem twice
			assert_err!(
				EthereumBacking::redeem(Origin::signed(id1.clone()), RedeemFor::Token, proof_record),
				<Error<Test>>::AssetAR,
			);
		});
}

#[test]
fn verify_linear_redeem_kton() {
	ExtBuilder::default()
		.build()
		.execute_with(|| {
			assert_ok!(EthereumRelay::set_number_of_blocks_safe(RawOrigin::Root.into(), 0));

			// https://ropsten.etherscan.io/tx/0x2878ae39a9e0db95e61164528bb1ec8684be194bdcc236848ff14d3fe5ba335d
			// darwinia: 5FP2eFNSVxJzSrE3N2NEVFPhUU34VzYFD6DDtRXbYzTdwPn8
			// hex: 0x92ae5b41feba5ee68a61449c557efa9e3b894a6461c058ec2de45429adb44546
			// amount: 0.123456789123456789 KTON
			let proof_record = EthereumReceiptProof {
				index: 0x4,
				proof: hex_bytes_unchecked(""),
				header_hash: fixed_hex_bytes_unchecked!("0x5f80ee45d62872fa4b0dbf779a8eb380e166ac80616d05875dd3e80e5fc40839", 32).into()
			};

			let header : EthereumHeader = serde_json::from_str(r#"{"parent_hash":"0x734ea7bd03f510e7dd0acc85a7ceb777d5d7d5ad5650785536fc09179a250143","timestamp":1599124483,"number":8610265,"author":"0x52351e33b3c693cc05f21831647ebdab8a68eb95","transactions_root":"0xd1c259805e50c5c4aa21b92f31d8a59f025f457a90a250ca516e8930d7bb05ec","uncles_hash":"0x1dcc4de8dec75d7aab85b567b6ccd41ad312451b948a7413f0a142fd40d49347","extra_data":"0x6c6f746f706f6f6c","state_root":"0xabb0917ee898c71f3826d97415ff9161b37d03146b54e1c7d536fabb23352a5c","receipts_root":"0x9873421df4b9e11b174bc08e4d79d9a65032ba702ea8d3e0e6c6e9217d1cbf03","log_bloom":"","gas_used":207923,"gas_limit":8000000,"difficulty":516043711,"seal":["0xa0a7ea34046ebd043b4bca8836bc8968113d026d6c852b80a83a9b06db2db938e5","0x8800118000012d2ccd"],"hash":"0x5f80ee45d62872fa4b0dbf779a8eb380e166ac80616d05875dd3e80e5fc40839"}"#).unwrap();

			assert_ok!(EthereumRelay::init_genesis_header(&header, 31419690269906095));

			let expect_account_id = EthereumBacking::account_id_try_from_bytes(
				&hex_bytes_unchecked("0xe44664996ab7b5d86c12e9d5ac3093f5b2efc9172cb7ce298cd6c3c51002c318"),
			).unwrap();
			// 0.123456789123456789 KTON
			assert_eq!(
				EthereumBacking::parse_token_redeem_proof(&proof_record),
				Ok((expect_account_id.clone(), (false, 123400), 0)),
			);

			let id1 = AccountId32::from([0; 32]);
			let kton_locked_before = EthereumBacking::pot::<Kton>();
			let _ = Kton::deposit_creating(&expect_account_id, 1);

			assert_ok!(EthereumBacking::redeem(
				Origin::signed(id1.clone()),
				RedeemFor::Token,
				proof_record.clone()
			));
			assert_eq!(Kton::free_balance(&expect_account_id), 123400 + 1);

			let kton_locked_after = EthereumBacking::pot::<Kton>();
			assert_eq!(kton_locked_after + 123400, kton_locked_before);

			// shouldn't redeem twice
			assert_err!(
				EthereumBacking::redeem(Origin::signed(id1.clone()), RedeemFor::Token, proof_record),
				<Error<Test>>::AssetAR,
			);
		});
}

#[test]
fn verify_linear_redeem_deposit() {
	ExtBuilder::default()
		.build()
		.execute_with(|| {
			assert_ok!(EthereumRelay::set_number_of_blocks_safe(RawOrigin::Root.into(), 0));

			// 1234ring -> 0.1234kton

			// _depositID    2
			// 0: address: 0xcC5E48BEb33b83b8bD0D9d9A85A8F6a27C51F5C5  _depositor
			// 1: uint128: 1001000000000000000000 _value
			// 2: uint128: 12 _months
			// 3: uint256: 1599125470 _startAt
			// 4: uint256: 1000 _unitInterest
			// 5: bool: false
			//  _data     0x92ae5b41feba5ee68a61449c557efa9e3b894a6461c058ec2de45429adb44546

			// transfer：https://ropsten.etherscan.io/tx/0x5a7004126466ce763501c89bcbb98d14f3c328c4b310b1976a38be1183d91919
			let proof_record = EthereumReceiptProof {
				index: 0x2e,
				proof: hex_bytes_unchecked(""),
				header_hash: fixed_hex_bytes_unchecked!("0x202591d2a7bff469ec186e3583e37b9c4bce2db847612ff975180436b5a4a1f1", 32).into()
			};

			let header : EthereumHeader = serde_json::from_str(r#"{"parent_hash":"0x870d2655fe393a3d12f595bce56ce5115907af9735cc8de8d95c05e7467d1321","timestamp":1599126312,"number":8610453,"author":"0x52351e33b3c693cc05f21831647ebdab8a68eb95","transactions_root":"0xd5a01c26eeb8e826613e4da09ac06f305fab7d183a84d417559139db814e2a23","uncles_hash":"0x1dcc4de8dec75d7aab85b567b6ccd41ad312451b948a7413f0a142fd40d49347","extra_data":"0x6c6f746f706f6f6c","state_root":"0x6d63278b3c9dae252148948ddd0db13ee1f7c81dc557681db22e422d37d9abf1","receipts_root":"0x707af65a83888ce8d9696941a122d3429c0600fb09c4644ab2162b1382099928","log_bloom":"","gas_used":7545472,"gas_limit":8000000,"difficulty":535275643,"seal":["0xa01548d3ecae87cb01cb142984086dc9f772b9d9094884269b1e3644ec108e52b5","0x880011800002104414"],"hash":"0x202591d2a7bff469ec186e3583e37b9c4bce2db847612ff975180436b5a4a1f1"}"#).unwrap();

			assert_ok!(EthereumRelay::init_genesis_header(&header, 31419789208662997));

			let ring_locked_before = EthereumBacking::pot::<Ring>();
			let expect_account_id = EthereumBacking::account_id_try_from_bytes(
				&hex_bytes_unchecked("0xe44664996ab7b5d86c12e9d5ac3093f5b2efc9172cb7ce298cd6c3c51002c318"),
			).unwrap();
			let id1 = AccountId32::from([0; 32]);
			let controller = AccountId32::from([1; 32]);
			let _ = Ring::deposit_creating(&expect_account_id, 1);

			assert_ok!(Call::from(<darwinia_staking::Call<Test>>::bond(
				controller.clone(),
				StakingBalance::RingBalance(1),
				RewardDestination::Controller,
				0,
			)).dispatch(Origin::signed(expect_account_id.clone())));
			assert_ok!(EthereumBacking::redeem(
				Origin::signed(id1.clone()),
				RedeemFor::Deposit,
				proof_record.clone()
			));
			assert_eq!(Ring::free_balance(&expect_account_id), 1001000000000 + 1);

			let ring_locked_after = EthereumBacking::pot::<Ring>();
			assert_eq!(ring_locked_after + 1001000000000, ring_locked_before);

			let staking_ledger = Staking::ledger(&controller);
			assert_eq!(staking_ledger, Some(StakingLedger {
				stash: expect_account_id,
				active_ring: 1001000000001,
				active_deposit_ring: 1001000000000,
				deposit_items: vec![TimeDepositItem {
					value: 1001000000000,
					start_time: 1599125470000,
					expire_time: 1630229470000,
				}],
				ring_staking_lock: StakingLock { staking_amount: 1001000000001, unbondings: vec![] },
				..Default::default()
			}));

			// shouldn't redeem twice
			assert_err!(
				EthereumBacking::redeem(Origin::signed(id1.clone()), RedeemFor::Deposit, proof_record),
				<Error<Test>>::AssetAR,
			);
		});
}
